mapper.insert:
INSERT INTO opt.optmz_cntr_mlss (
      flag, recd_dt, mark, open_stng_val, close_stng_val
) VALUES (
      #{mlssFlag} ,TIMESTAMP(DATE_FORMAT(#{mlssRecdDt}, '%Y%m%d%H%i%s')) ,#{mlssMark}, #{mlssOpenStngVal}, #{mlssCloseStngVal}
)
ON DUPLICATE KEY UPDATE
	open_stng_val	=	#{mlssOpenStngVal},
	close_stng_val 	=	#{mlssCloseStngVal}

INSERT INTO opt.optmz_cntr_pump ( flag, recd_dt, mark, m117a_ordr, m117b_ordr, m117c_ordr, m118_ordr, h5_hz, h4_hz, h3_hz, h2_hz, h1_hz
) VALUES (
#{pumpFlag} ,TIMESTAMP(DATE_FORMAT(#{pumpRecdDt}, '%Y%m%d%H%i%s')), #{pumpMark}, #{m117aOrdr}, #{m117bOrdr}, #{m117cOrdr}, #{m118Ordr}, #{h5Hz}, #{h4Hz}, #{h3Hz}, #{h2Hz}, #{h1Hz} ) ON DUPLICATE KEY UPDATE m117a_ordr = #{m117aOrdr}, m117b_ordr = #{m117bOrdr}, m117c_ordr = #{m117cOrdr}, m118_ordr = #{m118Ordr}, h5_hz = #{h5Hz}, h4_hz = #{h4Hz}, h3_hz = #{h3Hz}, h2_hz = #{h2Hz}, h1_hz = #{h1Hz}

