mapper.update:
update
opt.optmz_cntr_pump opt.optmz_cntr_psbr opt.optmz_cntr_denipho opt.optmz_cntr_pac opt.optmz_cntr_mlss
set mark='Y' where flag = 'opt' and mark <> 'Y' and recd_dt = #{recdDt}
